import React from 'react';
import Childcomponent from './child';
import AddComponent from './AddComponent';
class Mycomponent extends React.Component{
    state = {
        kynang: [
            {id: '1', name: 'php', nganh: '7'},
            {id: '2', name: 'html', nganh: '9'}, 
            {id: '3', name: 'css', nganh: '5'}
        ]
    }

    addNewUser = (user) => {
        console.log('>>> Check: ', user)
        this.setState({
            // arrUser: this.state.arrUser.push(user)
            arrUser: [...this.state.arrUser.u]
        })
        
    }

 
    

    render(){
        console.log('>>> Call render: ', this.state)
        return(
            <>
                {/* <div>Xin chào tôi tên là: {this.state['name']}</div>
                <div><b>Chuyên ngành:</b> {this.state.nganh}</div><br/> */}
                <AddComponent addNewUser={this.addNewUser}/>
               
                <Childcomponent kynang={this.state.kynang}/>
            </>
            
        )
    }
}
export default Mycomponent;
