import React from 'react';

class Childcomponent extends React.Component
{
    state = {
        showkynang: false
    }

    handleShowHiden = () =>
    {
        this.setState(
            {
                showkynang: !this.state.showkynang
            }
        )
    }

    render ()
    {
        // console.log( '>>> check pops: ', this.props )
        // let name = this.props.name;
        // let age = this.props.age;

        // khai báo nhanh
        let { name, age, kynang } = this.props;
        let { showkynang } = this.state;
        let check = showkynang === true ? 'showkynang = true' : 'showkynang = false';
        console.log( '>>> check status: ', check)
        return (
            <>
                <div>Xin chào tôi tên là: { name } - age: { age }</div>
                { showkynang === false ?
                    <div></div>
                    :
                    <>
                        <div>Kỹ năng:
                            {
                                kynang.map( ( item, index ) =>
                                {
                                    if ( +item.diem >= 7 )
                                    {
                                        return (
                                            <div key={ item.id }>
                                                { item.id } - { item.tenkn }
                                            </div>
                                        )
                                    }
                                } )
                            }
                        </div>
                        <button onClick={ () => this.handleShowHiden() }>Ẩn</button>
                    </>
                }
            </>
        )
    }
}


