        let { name, age, kynang } = this.props;
        let { showkynang } = this.state;
        return (
            <>
                <div>Xin chào tôi tên là: { name } - age: { age }</div>
                { showkynang === false &&
                    <button>Hiện</button>
                }
                { showkynang &&
                    <>
                        <button>Ẩn</button>
                        <div>Kỹ năng:
                            {
                                kynang.map( ( item, index ) =>
                                {
                                    if ( +item.diem >= 7 )
                                    {
                                        return (
                                            <div key={ item.id }>
                                                { item.id } - { item.tenkn }
                                            </div>
                                        )
                                    }
                                } )
                            }
                        </div>
                    </>
                }
            </>

        )
    }
}


