import React from 'react';
import Childcomponent from './child';
class Mycomponent extends React.Component{
    state = {
        name: '',
        nganh: '',
        kynang: [
            {id: 'kn1', tenkn: 'php', diem: ''},
            {id: 'kn2', tenkn: 'html'},
            {id: 'kn3', tenkn: 'css'}
        ]
    }

    handleOnChangeName = (event) =>{
        // console.log(event.target.value, 'event target: ', event.target, 'event object: ', event)
        this.setState({
            name: event.target.value
           
        })
    }

    handleOnChangeNganh = (event) =>{
        // console.log(event.target.value, 'event target: ', event.target, 'event object: ', event)
        this.setState({
            nganh: event.target.value
           
        })
    }

    handleClickButton = (event) =>{
        event.preventDefault()
        console.log('>>> Check data input: ', this.state)
        alert('Cick me!')
        
    }
    

    render(){
        console.log('>>> Call render: ', this.state)
        return(
            <>
                {/* <div>Xin chào tôi tên là: {this.state['name']}</div>
                <div><b>Chuyên ngành:</b> {this.state.nganh}</div><br/> */}
                <form>
                    <label>Họ tên:</label><br/>
                    <input type = "text" value={this.state.name} onChange={(event)=>this.handleOnChangeName(event)}></input> <br/>
                    <label>Chuyên ngành:</label><br/>
                    <input type = "text" value={this.state.nganh} onChange={(event)=>this.handleOnChangeNganh(event)}></input><br/>
                    <div >
                        <button style={{backgroundColor: 'pink', borderRadius: '1rem', border: 'none'}} onClick={(event) => this.handleClickButton(event)}>Button</button>
                    </div>
                </form>
                <Childcomponent name={this.state.name} age = {'24'} kynang={this.state.kynang}/>
            </>
            
        )
    }
}
export default Mycomponent;
