import React from 'react';

class Mycomponent extends React.Component{
    state = {
        name: 'Trần Thị Trúc Quyên',
        nganh: 'Hệ thống thông tin'
    }

    handleOnChangeName = (event) =>{
        // console.log(event.target.value, 'event target: ', event.target, 'event object: ', event)
        this.setState({
            name: event.target.value
           
        })
    }

    handleOnChangeNganh = (event) =>{
        // console.log(event.target.value, 'event target: ', event.target, 'event object: ', event)
        this.setState({
            nganh: event.target.value
           
        })
    }

    handleClickButton = () =>{
        
        alert('Cick me!')
    }
    

    render(){
        console.log('>>> Call render: ', this.state)
        return(
            <>
                {/* <div>Xin chào tôi tên là: {this.state['name']}</div>
                <div><b>Chuyên ngành:</b> {this.state.nganh}</div><br/> */}
                <label>Họ tên:</label>
                <input type = "text" value={this.state.name} onChange={(event)=>this.handleOnChangeName(event)}></input> 
                <label>Chuyên ngành:</label>
                <input type = "text" value={this.state.nganh} onChange={(event)=>this.handleOnChangeNganh(event)}></input>
                <div >
                    <button style={{backgroundColor: 'pink', borderRadius: '1rem', border: 'none'}} onClick={() => this.handleClickButton()}>Button</button>
                </div>
            </>
            
        )
    }
}
export default Mycomponent;
